import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FetchingData() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [formData, setFormData] = useState({});

  // Fetch existing data
  useEffect(() => {
    axios.get('http://localhost:8000/user/')
      .then(response => {
        setUsers(response.data);
      })
      .catch(err => console.error('Failed to fetch data:', err));
  }, []);

  // Function to add a new user
  const addNewUser = () => {
    const newUser = {
      ...formData,
      id: users.length > 0 ? Math.max(...users.map(user => user.id)) + 1 : 1,
      created_at: new Date().toISOString(),
      updated_at: null,
    };

    axios.post('http://localhost:8000/user/', newUser)
      .then(response => {
        console.log('New user added:', response.data);
        setUsers([...users, response.data]);
        setFormData({});
      })
      .catch(err => console.error('Failed to add user:', err));
  };

  // Function to update a user
  const updateUser = (userId, updatedData) => {
    axios.put(`http://localhost:8000/user/${userId}/`, updatedData)
      .then(response => {
        console.log('User updated:', response.data);
        setUsers(users.map(user => user.id === userId ? response.data : user));
        setFormData({});
        setSelectedUser(null);
      })
      .catch(err => console.error('Failed to update user:', err));
  };

  // Function to delete a user
  const deleteUser = (userId) => {
    axios.delete(`http://localhost:8000/user/${userId}/`)
      .then(() => {
        console.log(`User with ID ${userId} deleted.`);
        setUsers(users.filter(user => user.id !== userId));
      })
      .catch(err => console.error(`Failed to delete user with ID ${userId}:`, err));
  };

  // Event handler for form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Event handler for form submission
  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (selectedUser) {
      // Update the selected user with the form data
      updateUser(selectedUser.id, formData);
    } else {
      // Add a new user
      addNewUser();
    }
  };

  // Function to set selected user and pre-fill form data
  const handleEditButtonClick = (user) => {
    setSelectedUser(user);
    setFormData({
      name: user.name,
      username: user.username,
      password: user.password,
      email: user.email,
      phone: user.phone,
      address: user.address,
      city: user.city,
      zipcode: user.zipcode,
      company: user.company,
      website: user.website,
      role: user.role,
    });
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1 style={{ textAlign: 'center' }}>Fetching Data</h1>

      <ul>
        {users.map(user => (
          <li key={user.id}>
            {`${user.id} - ${user.username} - ${user.name} - ${user.password} - ${user.email} - ${user.phone} - ${user.address}, ${user.city}, ${user.zipcode} - ${user.company} - ${user.website} - ${user.role}`}
            {/* Edit and delete buttons */}
            <button onClick={() => handleEditButtonClick(user)}>Edit</button>
            <button onClick={() => deleteUser(user.id)}>Delete</button>
          </li>
        ))}
      </ul>

      <button onClick={() => setSelectedUser(null)}>Add New User</button>

      {/* Render form for updating or adding a user */}
      {selectedUser !== null || Object.keys(formData).length > 0 ? (
        <form onSubmit={handleFormSubmit} style={{ marginTop: '20px' }}>
          <div>
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Username:</label>
            <input
              type="text"
              name="username"
              value={formData.username || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Phone:</label>
            <input
              type="text"
              name="phone"
              value={formData.phone || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Address:</label>
            <input
              type="text"
              name="address"
              value={formData.address || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>City:</label>
            <input
              type="text"
              name="city"
              value={formData.city || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Zipcode:</label>
            <input
              type="text"
              name="zipcode"
              value={formData.zipcode || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Company:</label>
            <input
              type="text"
              name="company"
              value={formData.company || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Website:</label>
            <input
              type="url"
              name="website"
              value={formData.website || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Role:</label>
            <input
              type="text"
              name="role"
              value={formData.role || ''}
              onChange={handleInputChange}
            />
          </div>
          <button type="submit">
            {selectedUser ? 'Update User' : 'Add User'}
          </button>
        </form>
      ) : null}
    </div>
  );
}

export default FetchingData;
