import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FetchingData() {
  const [datas, setDatas] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [formData, setFormData] = useState({});

  // Fetch existing data
  useEffect(() => {
    axios.get('http://localhost:8000/user/')
      .then(response => {
        setDatas(response.data);
      })
      .catch(err => console.error('Failed to fetch data:', err));
  }, []);

  // Function to add a new user
  const addNewUser = () => {
    setSelectedUser(null); // Clear the selected user and show the form for adding a new user
    setFormData({}); // Clear form data
  };

  // Function to save the new user or update the existing user
  const saveUser = () => {
    const url = selectedUser ? `http://localhost:8000/user/${selectedUser.id}/` : 'http://localhost:8000/user/';
    const method = selectedUser ? 'put' : 'post';

    axios({
      method,
      url,
      data: {
        ...formData,
        id: selectedUser ? selectedUser.id : datas.reduce((maxId, data) => Math.max(maxId, data.id), 0) + 1,
        updated_at: new Date().toISOString(),
      },
    })
    .then(response => {
      if (selectedUser) {
        console.log('User updated:', response.data);
        // Update the user in the data list
        setDatas(datas.map(data => data.id === selectedUser.id ? response.data : data));
      } else {
        console.log('New user added:', response.data);
        // Add the new user to the data list
        setDatas([...datas, response.data]);
      }
      setSelectedUser(null); // Clear selected user
      setFormData({}); // Clear form data
    })
    .catch(err => console.error(`Failed to ${method === 'post' ? 'add' : 'update'} user:`, err));
  };

  // Function to delete a user
  const deleteUser = (userId) => {
    axios.delete(`http://localhost:8000/user/${userId}/`)
      .then(() => {
        console.log(`User with ID ${userId} deleted.`);
        // Filter out the deleted user from the state
        setDatas(datas.filter(data => data.id !== userId));
      })
      .catch(err => console.error(`Failed to delete user with ID ${userId}:`, err));
  };

  // Event handler for form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Event handler for form submission
  const handleFormSubmit = (e) => {
    e.preventDefault();
    saveUser();
  };

  // Function to set selected user and pre-fill form data
  const handleEditButtonClick = (user) => {
    setSelectedUser(user);
    setFormData({
      name: user.name,
      username: user.username,
      password: user.password,
      email: user.email,
      phone: user.phone,
      address: user.address,
      city: user.city,
      zipcode: user.zipcode,
      company: user.company,
      website: user.website,
      role: user.role,
    });
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1 style={{ textAlign: 'center' }}>Crud Data Process</h1>
      <ul>
        {datas.map((data) => (
          <li key={data.id}>
            {data.id} - {data.username} - {data.name} - {data.password} - {data.email} - {data.phone} - {data.address}, {data.city}, {data.zipcode} - {data.company} - {data.website} - {data.role}
            {/* Add a button for updating this user */}
            <button onClick={() => handleEditButtonClick(data)}>Edit</button>
            {/* Add a button for deleting this user */}
            <button onClick={() => deleteUser(data.id)}>Delete</button>
          </li>
        ))}
      </ul>
      {/* Button to add a new user */}
      <button onClick={addNewUser} style={{ display: 'block', margin: '0 auto' }}>Add New User</button>

      {/* Render form for updating or adding a user */}
      {selectedUser !== null || Object.keys(formData).length > 0 ? (
        <form onSubmit={handleFormSubmit} style={{ marginTop: '20px' }}>
          <div>
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Username:</label>
            <input
              type="text"
              name="username"
              value={formData.username || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Phone:</label>
            <input
              type="text"
              name="phone"
              value={formData.phone || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Address:</label>
            <input
              type="text"
              name="address"
              value={formData.address || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>City:</label>
            <input
              type="text"
              name="city"
              value={formData.city || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Zipcode:</label>
            <input
              type="text"
              name="zipcode"
              value={formData.zipcode || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Company:</label>
            <input
              type="text"
              name="company"
              value={formData.company || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Website:</label>
            <input
              type="url"
              name="website"
              value={formData.website || ''}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <label>Role:</label>
            <input
              type="text"
              name="role"
              value={formData.role || ''}
              onChange={handleInputChange}
            />
          </div>
          <button type="submit">
            {selectedUser ? 'Update User' : 'Add User'}
          </button>
        </form>
      ) : null}
    </div>
  );
}

export default FetchingData;
