import React, { useState, useEffect } from 'react';
import axios from 'axios';

function FetchingData() {
  const [datas, setDatas] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [formData, setFormData] = useState({});

  // Fetch existing data
  useEffect(() => {
    axios.get('http://localhost:8000/user/')
      .then(response => {
        setDatas(response.data);
      })
      .catch(err => console.error('Failed to fetch data:', err));
  }, []);

  // Function to add a new user
  const addNewUser = () => {
    const newId = datas.reduce((maxId, data) => Math.max(maxId, data.id), 0) + 1;

    axios.post('http://localhost:8000/user/', {
      id: newId,
      name: 'John Doe',
      username: 'johndoe',
      password: 'password123',
      email: 'john.doe@example.com',
      phone: '1234567890',
      address: '123 Main St',
      city: 'Anytown',
      zipcode: '12345',
      company: 'Example Company',
      website: 'www.example.com',
      role: 'user',
      created_at: new Date().toISOString(),
      updated_at: null,
      deleted_at: null,
    })
      .then(response => {
        console.log('New user added:', response.data);
        setDatas([...datas, response.data]);
      })
      .catch(err => console.error('Failed to add user:', err));
  };

  // Function to update a user
  const updateUser = (userId, updatedData) => {
    axios.put(`http://localhost:8000/user/${userId}/`, updatedData)
      .then(response => {
        console.log('User updated:', response.data);
        // Update the data in the state
        setDatas(datas.map(data => data.id === userId ? response.data : data));
      })
      .catch(err => console.error('Failed to update user:', err));
  };

  // Function to delete a user
  const deleteUser = (userId) => {
    axios.delete(`http://localhost:8000/user/${userId}/`)
      .then(() => {
        console.log(`User with ID ${userId} deleted.`);
        // Filter out the deleted user from the state
        setDatas(datas.filter(data => data.id !== userId));
      })
      .catch(err => console.error(`Failed to delete user with ID ${userId}:`, err));
  };

  // Event handler for form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Event handler for form submission
  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (selectedUser) {
      // Update the selected user with the new form data
      updateUser(selectedUser.id, formData);
    }
    // Reset the form data and selected user
    setFormData({});
    setSelectedUser(null);
  };

  // Function to set selected user and pre-fill form data
  const handleEditButtonClick = (user) => {
    setSelectedUser(user);
    setFormData({
      name: user.name,
      username: user.username,
      email: user.email,
      phone: user.phone,
      address: user.address,
      city: user.city,
      zipcode: user.zipcode,
      company: user.company,
      website: user.website,
      role: user.role,
      updated_at: user.updated_at,
    });
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1 style={{ textAlign: 'center' }}>Fetching Data</h1>
      <ul>
        {datas.map((data) => (
          <li key={data.id}>
            {data.id} - {data.username} - {data.name} - {data.password} - {data.email} - {data.phone} - {data.address}, {data.city}, {data.zipcode} - {data.company} - {data.website} - {data.role} - {data.created_at} - {data.updated_at} - {data.deleted_at}
            {/* Add a button for updating this user */}
            <button onClick={() => handleEditButtonClick(data)}>Edit</button>
            {/* Add a button for deleting this user */}
            <button onClick={() => deleteUser(data.id)}>Delete</button>
          </li>
        ))}
      </ul>
      <button onClick={addNewUser} style={{ display: 'block', margin: '0 auto' }}>Add New User</button>

      {/* Render the update form when a user is selected */}
      {selectedUser && (
        <form onSubmit={handleFormSubmit} style={{ marginTop: '20px' }}>
          <div>
            <label>Name:</label>
            <input type="text" name="name" value={formData.name || ''} onChange={handleInputChange} />
          </div>
          <div>
            <label>Username:</label>
            <input type="text" name="username" value={formData.username || ''} onChange={handleInputChange} />
          </div>
          <div>
            <label>Email:</label>
            <input type="text" name="email" value={formData.email || ''} onChange={handleInputChange} />
          </div>
          <div>
            <label>Phone:</label>
            <input type="text" name="phone" value={formData.phone || ''} onChange={handleInputChange} />
          </div>
          <div>
            <label>Address:</label>
            <input type="text" name="address" value={formData.address || ''} onChange={handleInputChange} />
          </div>
          <div>
            <label>City:</label>
            <input type="text" name="city" value={formData.city || ''} onChange={handleInputChange} />
          </div>
          <div>
            <label>Zipcode:</label>
            <input type="text" name="zipcode" value={formData.zipcode || ''} onChange={handleInputChange} />
          </div>
          <div>
            <label>Company:</label>
            <input type="text" name="company" value={formData.company || ''} onChange={handleInputChange} />
          </div>
          <div>
            <label>Website:</label>
            <input type="text" name="website" value={formData.website || ''} onChange={handleInputChange} />
          </div>
          <div>
            <label>Role:</label>
            <input type="text" name="role" value={formData.role || ''} onChange={handleInputChange} />
          </div>
          <button type="submit">Update User</button>
        </form>
      )}
    </div>
  );
}

export default FetchingData;
