import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Define UserForm component
const UserForm = ({ formData, handleInputChange, handleSubmit, selectedUser, roles }) => {
    const formFields = [
        { name: 'name', label: 'Name', type: 'text' },
        { name: 'username', label: 'Username', type: 'text' },
        { name: 'password', label: 'Password', type: 'password' },
        { name: 'email', label: 'Email', type: 'email' },
        { name: 'phone', label: 'Phone', type: 'text' },
        { name: 'address', label: 'Address', type: 'text' },
        { name: 'city', label: 'City', type: 'text' },
        { name: 'zipcode', label: 'Zipcode', type: 'text' },
        { name: 'company', label: 'Company', type: 'text' },
        { name: 'website', label: 'Website', type: 'url' },
        { name: 'role', label: 'Role', type: 'select' }, // Changed type to 'select' for dropdown
    ];

    return (
        <form onSubmit={handleSubmit}>
            {formFields.map(({ name, label, type }) => (
                <div key={name}>
                    <label>{label}:</label>
                    {type === 'select' ? (
                        // Render dropdown list for 'role'
                        <select name={name} value={formData[name] || ''} onChange={handleInputChange}>
                            <option value="" disabled>Select a role</option>
                            {roles.map((role) => (
                                <option key={role.id} value={role.name}>
                                    {role.name}
                                </option>
                            ))}
                        </select>
                    ) : (
                        <input
                            type={type}
                            name={name}
                            value={formData[name] || ''}
                            onChange={handleInputChange}
                        />
                    )}
                </div>
            ))}
            <button type="submit">{selectedUser ? 'Update User' : 'Add User'}</button>
        </form>
    );
};

function FetchingData() {
    const [datas, setDatas] = useState([]);
    const [roles, setRoles] = useState([]); // State to store roles
    const [selectedUser, setSelectedUser] = useState(null);
    const [formData, setFormData] = useState({});

    // Fetch existing data
    useEffect(() => {
        axios.get('http://localhost:8000/user/')
            .then(response => {
                setDatas(response.data);
            })
            .catch(err => console.error('Failed to fetch data:', err));
    }, []);

    // Fetch roles data
    useEffect(() => {
        axios.get('http://localhost:8000/roles/')
            .then(response => {
                setRoles(response.data);
            })
            .catch(err => console.error('Failed to fetch roles:', err));
    }, []);

    // Function to handle form input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Function to add or update a user
    const handleSubmit = (e) => {
        e.preventDefault();

        const isNewUser = !selectedUser; // Determine if adding or updating a user
        const url = isNewUser ? 'http://localhost:8000/user/' : `http://localhost:8000/user/${selectedUser.id}/`;
        const method = isNewUser ? 'post' : 'put';

        axios({
            method,
            url,
            data: {
                ...formData,
                id: isNewUser ? datas.reduce((maxId, data) => Math.max(maxId, data.id), 0) + 1 : selectedUser.id,
                updated_at: isNewUser ? null : new Date().toISOString(),
            },
        })
        .then(response => {
            const user = response.data;

            if (isNewUser) {
                console.log('New user added:', user);
                // Add the new user to the data list
                setDatas([...datas, user]);
            } else {
                console.log('User updated:', user);
                // Update the user in the data list
                setDatas(datas.map(data => data.id === selectedUser.id ? user : data));
            }
            // Reset the form
            setSelectedUser(null);
            setFormData({});
        })
        .catch(err => console.error(`Failed to ${method === 'post' ? 'add' : 'update'} user:`, err));
    };

    // Function to delete a user
    const deleteUser = (userId) => {
        axios.delete(`http://localhost:8000/user/${userId}/`)
            .then(() => {
                console.log(`User with ID ${userId} deleted.`);
                // Remove the deleted user from the data list
                setDatas(datas.filter(data => data.id !== userId));
            })
            .catch(err => console.error(`Failed to delete user with ID ${userId}:`, err));
    };

    // Function to prepare the form data for editing a user
    const handleEditButtonClick = (user) => {
        setSelectedUser(user);
        setFormData({
            name: user.name,
            username: user.username,
            password: user.password,
            email: user.email,
            phone: user.phone,
            address: user.address,
            city: user.city,
            zipcode: user.zipcode,
            company: user.company,
            website: user.website,
            role: user.role,
        });
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <h1 style={{ textAlign: 'center' }}>CRUD Data Process</h1>
            <ul>
                {datas.map((data) => (
                    <li key={data.id}>
                        {data.id} - {data.username} - {data.name} - {data.password} - {data.email} - {data.phone} - {data.address}, {data.city}, {data.zipcode} - {data.company} - {data.website} - {data.role}
                        {/* Edit button */}
                        <button onClick={() => handleEditButtonClick(data)}>Edit</button>
                        {/* Delete button */}
                        <button onClick={() => deleteUser(data.id)}>Delete</button>
                    </li>
                ))}
            </ul>
            {/* Button to add a new user */}
            <button onClick={() => setSelectedUser(null)} style={{ marginBottom: '20px' }}>Add New User</button>

            {/* Render form for updating or adding a user */}
            {(selectedUser !== null || Object.keys(formData).length > 0) && (
                <UserForm formData={formData} handleInputChange={handleInputChange} handleSubmit={handleSubmit} selectedUser={selectedUser} roles={roles} />
            )}
        </div>
    );
}

export default FetchingData;
