import React, { useState, useEffect } from 'react';
import './scss/App.css';


function App() {
  const [cityName, setCityName] = useState('');
  const [commentsList, setCommentsList] = useState([]);
  const [users, setUsers] = useState([]);
  const [cityPropertyCount, setCityPropertyCount] = useState([]);
  const [allCities, setAllCities] = useState([]);

  // Fetch data once on mount
  useEffect(() => {
    // Fetch comments
    async function fetchComments() {
      try {
        const response = await fetch('/api/comments');
        const data = await response.json();
        setCommentsList(data);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    }

    // Fetch users
    async function fetchUsers() {
      try {
        const response = await fetch('/api/users');
        const data = await response.json();
        setUsers(data);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    }

    // Fetch city property count
    async function fetchCityPropertyCount() {
      try {
        const response = await fetch('/api/city-property-count');
        const data = await response.json();
        setCityPropertyCount(data);
      } catch (error) {
        console.error('Error fetching city property count:', error);
      }
    }

    // Fetch all cities
    async function fetchAllCities() {
      try {
        const response = await fetch('/api/cities');
        const data = await response.json();
        setAllCities(data);
      } catch (error) {
        console.error('Error fetching all cities:', error);
      }
    }

    // Call fetch functions
    fetchComments();
    fetchUsers();
    fetchCityPropertyCount();
    fetchAllCities();
  }, []);

  // Event handler
  const handleCityNameChange = (e) => setCityName(e.target.value);

  return (
    <div>
      {/* Header */}
      <div className="container-fluid header bg-white p-0">
        <div className="row g-0 align-items-center flex-column-reverse flex-md-row">
          <div className="col-md-12 p-5 mt-lg-5 homeHeaderFind">
            <h1 className="display-5 animated fadeIn mb-4 text-center">
              Find And <span className="text-primary">book your ideal room</span> for months
            </h1>
            <p className="animated fadeIn mb-4 pb-2 text-center">Test text</p>
          </div>
        </div>
      </div>

      {/* Search Form */}
      <form method="post">
        <div id="searchDiv" className="container-fluid bg-primary mb-5 wow fadeIn" data-wow-delay="0.1s" style={{ padding: '35px' }}>
          <div className="container">
